import { actionType } from "../../configs";

interface TItem {
  id: string;
  text: string;
}

interface TState {
  lists: TItem[];
  favorites: TItem[];
}

interface TAction {
  type: string;
  item: TItem;
}

const initState: TState = {
  lists: [],
  favorites: [],
};

const reducer = (state: TState = initState, action: TAction) => {
  switch (action.type) {
    case actionType.ADD_LIST:
      return {
        ...state,
        items: state.lists.concat([action.item]),
      };
    case actionType.REMOVE_LIST:
      return {
        ...state,
        items: state.lists.filter((item) => item.id !== action.item.id),
      };
    case actionType.ADD_FAVORITE:
      return {
        ...state,
        favorite: state.favorites.concat([action.item]),
      };
    case actionType.REMOVE_FAVORITE:
      return {
        ...state,
        favorite: state.favorites.filter((item) => item.id !== action.item.id),
      };
    default:
      return state;
  }
};

export default reducer;
